    private void btnFormatActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFormatActionPerformed
        // TODO add your handling code here:
        String text = txtDisplay.getText();
        Font f = txtDisplay.getFont();
        Color textColor = txtDisplay.getForeground();
        Color backroundColor = txtDisplay.getBackground();
        (new secondfont(this, text, f, textColor, backroundColor)).setVisible(true);
    }//GEN-LAST:event_btnFormatActionPerformed

    /**
     * @param args the command line arguments
     */
    
    public void Format(Font f, Color textColor, Color backgroundColor){
        txtDisplay.setFont(f);
        txtDisplay.setForeground(textColor);
        txtDisplay.setBackground(backgroundColor);
    }
    
